import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import ListItem from "@material-ui/core/ListItem";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import Checkbox from "@material-ui/core/Checkbox";
import IconButton from "@material-ui/core/IconButton";
import CommentIcon from "@material-ui/icons/Comment";
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogActions from "@material-ui/core/DialogActions";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

import list from "./data/list";

const styles = theme => ({
  root: {
    listStyleType: "none",
    width: "100%",
    maxWidth: 360,
    minWidth: 300,
    backgroundColor: theme.palette.background.paper,
    display: "flex",
    flexDirection: "column"
  }
});

class List extends React.Component {
  state = {
    checked: [0],
    openInfo: false,
    openAdd: false
  };

  handleCloseInfo = () => {
    this.setState({
      openInfo: false
    });
  };

  handleOpenInfo = () => {
    this.setState({
      openInfo: true
    });
  };
  handleToggle = value => () => {
    const { checked } = this.state;
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    this.setState({
      checked: newChecked
    });
  };
  render() {
    const { classes } = this.props;

    const shoppingList = list.map((item, index) => (
      <ListItem
        key={index}
        role={undefined}
        dense
        button
        onClick={this.handleToggle(item)}
      >
        <Checkbox
          checked={this.state.checked.indexOf(item) !== -1}
          tabIndex={-1}
          disableRipple
        />
        <ListItemText primary={item.name} />
        <ListItemSecondaryAction>
          <IconButton aria-label="Info" onClick={this.handleOpenInfo}>
            <CommentIcon />
          </IconButton>
        </ListItemSecondaryAction>
      </ListItem>
    ));

    return (
      <div className={classes.root}>
        <Dialog open={this.state.openInfo} onClose={this.handleCloseInfo}>
          <DialogTitle>Alerts</DialogTitle>
          <DialogContent>
            <DialogContentText>Buy in Lidl</DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button color="primary" onClick={this.handleCloseInfo}>
              Close
            </Button>
          </DialogActions>
        </Dialog>
        <Dialog open={this.state.openAdd} onClose={this.handleCloseAdd}>
          <DialogTitle>Add a new product</DialogTitle>
          <TextField
            required
            id="outlined-required"
            label="Type product"
            defaultValue=""
            className={classes.textField}
            margin="normal"
            variant="outlined"
            onChange={this.changeNewItem}
          />
          <DialogActions>
            <Button color="primary" onClick={this.handleCloseAdd}>
              Add
            </Button>
          </DialogActions>
        </Dialog>
        {shoppingList}
        <Button color="primary" onClick={this.handleOpenAddItem}>
          Add new item
        </Button>
      </div>
    );
  }
}

List.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(List);
