const appRouter = app => {
    app.get("/", function(req, res) {
      res.status(200).send("Welcome to our restful API");
    });
    app.get("/store", function (req, res) {
        const store = ({
            "list": [
              {
                "name": "bread",
                "info": "Buy in Lidl"
              },
              {
                "name": "milk",
                "info": ""
              },
              {
                "name": "potatoes",
                "info": "Buy in Tesco"
              },
              {
                "name": "beer",
                "info": ""
              }
            ],
            "activeInfo": 0,
            "checked": [],
            "openInfo": "false",
            "openAdd": "false"
          });
          res.status(200).send(store);
  })}

  module.exports = appRouter;
