import React, { Component } from "react";
import PropTypes from "prop-types";

import { connect } from "react-redux";

import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogActions from "@material-ui/core/DialogActions";
import TextField from "@material-ui/core/TextField";

import List from "./list";

import list from "./data/list";

const styles = theme => ({
  root: {
    width: "100%",
    maxWidth: 360,
    minWidth: 300,
    backgroundColor: theme.palette.background.paper
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit
  }
});

class Checkbox extends Component {

  state = {
    checked: [],
    // openInfo: false,
    openAdd: false,
    newItem: "",
    newItemInfo: ""
  };

  render() {
    const {
      classes,
      checked,
      openInfo,
      openAdd,
      newItem,
      newItemInfo,
      handleOpenInfo
    } = this.props;

    console.log(this.props.openInfo);

    return (
      <div className={classes.checkList}>
        <List />
        <Dialog open={openInfo} onClose={handleOpenInfo}>
          <DialogTitle>More info</DialogTitle>
          <DialogContent>
            <DialogContentText>Buy in Lidl</DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button color="primary" onClick={this.handle}>
              Close
            </Button>
          </DialogActions>
        </Dialog>
        <Dialog open={this.state.openAdd} onClose={null}>
          <DialogTitle>Add a new product</DialogTitle>
          <TextField
            required
            id="outlined-required"
            label="New item"
            defaultValue=""
            className={classes.textField}
            margin="normal"
            variant="outlined"
            onChange={this.changeNewItem}
          />
          <TextField
            id="outlined"
            label="Additional info"
            defaultValue=""
            className={classes.textField}
            margin="normal"
            variant="outlined"
            onChange={this.changeNewItemInfo}
          />
          <DialogActions>
            <Button color="primary" onClick={this.handleCloseAdd}>
              Add
            </Button>
          </DialogActions>
        </Dialog>
        <Button color="primary" onClick={this.handleOpenAddItem}>
          Add new item
        </Button>
      </div>
    );
  }
}

Checkbox.propTypes = {
  classes: PropTypes.object.isRequired,
  // checked: PropTypes.boolean,
  openInfo: PropTypes.bool
  // openAdd: PropTypes.boolean,
  // newItem: PropTypes.object,
  // newItemInfo: PropTypes.string
};


const mapStateToProps = state => {
  return { openInfo: state.openInfo };
};

const mapDispatchToProps = dispatch => {
  return {
    handleOpenInfo: () => dispatch({ type: 'INFO' }),
  }
};

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(styles)(Checkbox));
