import { createStore } from "redux";


const reducer = (state, action) => {
    switch (action.type) {
      case "INFO":
      console.log("action.type")
        return { ...state, openInfo: (state.openInfo = false ? true : false) };
      default:
        return state;
    }
  };

const store = createStore(reducer, {
    checked: "",
    openInfo: false,
    openAdd: false,
    newItem: "",
    newItemInfo: ""
  });

  export default store;
