const reducer = (state, action) => {
  switch (action.type) {
    case "INFO":
      return { ...state, openInfo: state.openInfo ? false : true };
    case "ADD":
      return { ...state, openAdd: state.openAdd ? false : true };
    default:
      return state;
  }
};

export default reducer;
